sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (isLoading && !g_form.isNewRecord() || newValue === '' || newValue === oldValue) {
        return;
    }
	else {
		var getEmailAddress = new GlideAjax("GetEmailAddress");
		getEmailAddress.addParam("sysparm_name", "getEmailForUser");
		getEmailAddress.addParam("sysparm_userID", g_form.getValue("u_requested_for"));
		getEmailAddress.getXML(populateEmailAddressForTheUser);		
	}

	function populateEmailAddressForTheUser(response) {
		g_form.clearValue("u_requested_for_email");
		g_form.setValue("u_requested_for_email", response.responseXML.documentElement.getAttribute("answer"));
	}
}
