sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    jslog(control.value + " -- " + oldValue + " -- " + newValue + " -- " + isLoading + " -- " + isTemplate);
    if (control === "u_request_type") {
        switch (newValue) {
            case "human resources":
                jslog("You selectected HR");
                break;

            case "facilities":
                jslog("You selectected Facilities");
                break;

            case "legal":
                jslog("You selectected Legal");
                break;

            case "your_choice":
                jslog("You selectected Your Department Choice");
                break;
        }
    }
}
