sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    var whatNeeded = "u_choice_1";
    var whatNeededControl = g_form.getControl(whatNeeded);
    var whatNeededOldOptions = whatNeeded.options;
    var whatNeededValue = g_form.getValue(whatNeeded);

    if (newValue === '') {
        return;
    }
    else {
        g_form.clearOptions(whatNeeded);
        var apiResponseEntry = { url: "", name: "" };
        //getApiSingleEntry();
        switch (newValue) {
            case "human resources":
                g_form.addOption(whatNeeded, "hr1", "Human Resource 1");
                g_form.addOption(whatNeeded, "hr2", "Human Resource 2");
                g_form.addOption(whatNeeded, apiResponseEntry.url, apiResponseEntry.name);
                break;

            case "facilities":
                g_form.addOption(whatNeeded, "facilities1", "Facilities 1");
                g_form.addOption(whatNeeded, "facilities2", "Facilities 2");
                g_form.addOption(whatNeeded, apiResponseEntry.url, apiResponseEntry.name);
                break;

            case "legal":
                g_form.addOption(whatNeeded, "legal1", "Legal 1");
                g_form.addOption(whatNeeded, "legal2", "Legal 2");
                g_form.addOption(whatNeeded, apiResponseEntry.url, apiResponseEntry.name);
                break;

            case "your_choice":
                g_form.addOption(whatNeeded, "your_choice1", "Your Department Choice 1");
                g_form.addOption(whatNeeded, "your_choice2", "Your Department Choice 2");
                g_form.addOption(whatNeeded, apiResponseEntry.url, apiResponseEntry.name);
                break;
        }
    }
    if (isLoading && !g_form.isNewRecord()) {
        g_form.setValue('u_choice_1', whatNeededValue);
    }
}

function getApiSingleEntry() {
    try {
        var r = new sn_ws.RESTMessageV2('x_194306_needit.HardikPublicAPISet1', 'Http Post With Application/JSON Header');

        //override authentication profile 
        authentication_type = 'basic';
        r.setAuthentication(authentication_type, "HardikAuthProfile1");

        var response = r.execute();
        var responseBody = response.getBody();
        var httpStatus = response.getStatusCode();
    }
    catch (ex) {
        alert("Error: " + ex.message);
    }
}
