sys_script_email.script:
(function runMailScript(/* GlideRecord */ current, /* TemplatePrinter */ template,
          /* Optional EmailOutbound */ email, /* Optional GlideRecord */ email_action,
          /* Optional GlideRecord */ event) {

	var rightNow = new GlideTime();

	var overdueNeedItTasks = new GlideRecord("x_194306_needit_needit_task");
	overdueNeedItTasks.addQuery("due_date", "<=", rightNow);
	overdueNeedItTasks.addQuery("state", "<", 3);
	overdueNeedItTasks.addQuery("assigned_to", current.assigned_to);
	overdueNeedItTasks.query();

	var overdueNeedItTaskCount = overdueNeedItTasks.getRowCount() - 1;

	if(overdueNeedItTaskCount > 0)
		template.print("In addition to this overdue NeedIt task, you have " + overdueNeedItTaskCount + " more overdue Tasks.");          
})(current, template, email, email_action, event);
