sys_script_include.script:
var NeedItUtils = Class.create();

NeedItUtils.prototype = { 
	initialize: function(){		
	},

	isPastDate : function(date){
		var currentDate = new GlideDate();
		var givenDate = new GlideDateTime(date).getDate();

		return givenDate.before(currentDate);
	},

	isCurrentDate : function(date){
		var currentDate = new GlideDateTime().getDate();
		var givenDate = new GlideDateTime(date).getDate();
		return givenDate == currentDate;
	},

	type: "NeedItUtils"
};
