sysauto_script.script:
var autoCloseThreshold = gs.getProperty("x_194306_needit.autoCloseOverdue");

var overdueNeedItRecord = new GlideRecord("x_194306_needit_needit_task");
overdueNeedItRecord.addQuery("due_date", "<=", gs.daysAgo(autoCloseThreshold));
overdueNeedItRecord.addQuery("state", "<", 3);
overdueNeedItRecord.query();

if(overdueNeedItRecord.next()){
	gs.info("Auto Closing the NeedIt Task # " + overdueNeedItRecord.number);
	overdueNeedItRecord.work_notes = "Record was auto closed because it was beyond the set Due Date.";
	overdueNeedItRecord.state = 4;
	overdueNeedItRecord.update();
}
